                                        <img class='img_logo_news' src='uploads_news/" . htmlspecialchars($row['logo_path']) . "' alt=''>
                                    
                                    <div class='um16_gray'>" . htmlspecialchars($row['date']) . "</div>
                                </div>
                                <div class='um26_black'>" . htmlspecialchars($row['title']) . "</div>
